CREATE TABLE IF NOT EXISTS medicine (
    medicine_id SERIAL PRIMARY KEY,
    name VARCHAR(255) NOT NULL,                   -- Preparato (sugalvotas) pavadinimas
    active_substance VARCHAR(255) NOT NULL,       -- Veiklioji (-osios) medžiaga (-os)
    strength VARCHAR(255) NOT NULL,               -- Stiprumas
    formative_form_id INT NOT NULL,               -- Farmacinė forma
    use_method_id INT NOT NULL,                   -- Vartojimo būdas
    drug_type_id INT NOT NULL,                    -- Vaisto tipas (receptinis, nereceptinis)
    atc_code CHAR(7) NOT NULL,                    -- ATC kodas (-ai)
    atc_name VARCHAR(255) NOT NULL,               -- ATC kodo pavadinimas
    drug_subgroup_id INT,
    quantity VARCHAR(200),
    paper VARCHAR(255),
    base_price DECIMAL(8,2),
    drug_use_instructions TEXT DEFAULT NULL       -- Naudojimo instrukcijos
);

ALTER TABLE medicine
  ADD CONSTRAINT medicine_con1 FOREIGN KEY (formative_form_id) REFERENCES formative_form (formative_form_id),
  ADD CONSTRAINT medicine_con2 FOREIGN KEY (use_method_id) REFERENCES use_method (use_method_id),
  ADD CONSTRAINT medicine_con3 FOREIGN KEY (drug_type_id) REFERENCES drug_type (drug_type_id),
  ADD CONSTRAINT medicine_con4 FOREIGN KEY (drug_subgroup_id) REFERENCES drug_subgroup (drug_subgroup_id);


