INSERT INTO medicine VALUES(131, 'STREPSILS Vitamin C', '2,4-dichlorobenzilo alkoholis/Amilmetakrezolis/Askorbo rūgštis', '1,2 mg/0,6 mg/100 mg', -1, 1.0, 1.0, '-1', '-1', NULL, 'N24', 'https://vapris.vvkt.lt/vvkt-web/public/medications/view/13830', '-1', 'Visada vartokite šį vaistą tiksliai kaip aprašyta šiame lapelyje arba kaip nurodė gydytojas arba vaistininkas. Jeigu abejojate, kreipkitės į gydytoją arba vaistininką.
 
