INSERT INTO medicine VALUES(135, 'VELIOS', 'Diosminas', '1000 mg', -1, 1.0, 1.0, '-1', '-1', NULL, 'N30', 'https://vapris.vvkt.lt/vvkt-web/public/medications/view/26530', '-1', 'Visada vartokite šį vaistą tiksliai kaip aprašyta šiame lapelyje arba kaip nurodė gydytojas arba vaistininkas. Jeigu abejojate, kreipkitės į gydytoją arba vaistininką.
