INSERT INTO medicine VALUES(140, 'OROPERIDYS', 'Domperidonas', '10 mg', -1, 1.0, 2.0, '-1', '-1', NULL, 'N21', 'https://vapris.vvkt.lt/vvkt-web/public/medications/view/19139', '-1', 'Visada vartokite šį vaistą tiksliai kaip aprašyta šiame lapelyje arba kaip nurodė gydytojas. Jeigu abejojate, kreipkitės į gydytoją arba vaistininką.
 
