INSERT INTO medicine VALUES(176, 'CARDURA XL', 'Doksazosinas', '8 mg', -1, 1.0, 2.0, '-1', '-1', NULL, 'N30', 'https://vapris.vvkt.lt/vvkt-web/public/medications/view/9755', '-1', 'Visada vartokite šį vaistą tiksliai kaip nurodė gydytojas arba vaistininkas. Jeigu abejojate, kreipkitės į gydytoją arba vaistininką.
