INSERT INTO medicine VALUES(265, 'CETIRIZIN ACTAVIS', 'Cetirizino dihidrochloridas', '10 mg', -1, 1.0, 2.0, '-1', '-1', NULL, 'N30', 'https://vapris.vvkt.lt/vvkt-web/public/medications/view/12879', '-1', 'Visada vartokite šį vaistą tiksliai kaip nurodė gydytojas arba vaistininkas. Jeigu abejojate, kreipkitės į gydytoją arba vaistininką.
