INSERT INTO medicine VALUES(379, 'TAMSULOSIN LANNACHER', 'Tamsulozino hidrochloridas', '0,4 mg', -1, 1.0, 2.0, '-1', '-1', NULL, 'N30', 'https://vapris.vvkt.lt/vvkt-web/public/medications/view/16292', '-1', 'Visada vartokite šį vaistą tiksliai kaip nurodė gydytojas. Jeigu abejojate, kreipkitės į gydytoją arba vaistininką.
 
