INSERT INTO medicine VALUES(386, 'LORATIN', 'Loratadinas', '10 mg', -1, 1.0, 2.0, '-1', '-1', NULL, 'N30', 'https://vapris.vvkt.lt/vvkt-web/public/medications/view/31190', '-1', 'Visada vartokite šį vaistą tiksliai kaip nurodė gydytojas. Jeigu abejojate, kreipkitės į gydytoją arba vaistininką.
 
