INSERT INTO medicine VALUES(387, 'METINDOL', 'Indometacinas', '75 mg', -1, 1.0, 2.0, '-1', '-1', NULL, 'N50', 'https://vapris.vvkt.lt/vvkt-web/public/medications/view/6551', '-1', 'Visada vartokite šį vaistą tiksliai kaip nurodė gydytojas. Jeigu abejojate, kreipkitės į gydytoją arba vaistininką.
 
