INSERT INTO medicine VALUES(388, 'SITAGLIPTIN TEVA', 'Sitagliptinas', '100 mg', -1, 1.0, 2.0, '-1', '-1', NULL, 'N28', 'https://vapris.vvkt.lt/vvkt-web/public/medications/view/25605', '-1', 'Visada vartokite šį vaistą tiksliai kaip nurodė gydytojas. Jeigu abejojate, kreipkitės į gydytoją arba vaistininką.
 
