INSERT INTO medicine VALUES(408, 'IBUFEN', 'Ibuprofenas', '100 mg/5 ml', -1, 1.0, 2.0, '-1', '-1', NULL, '100 g', 'https://vapris.vvkt.lt/vvkt-web/public/medications/view/8970', '-1', 'Visada vartokite šį vaistą tiksliai kaip nurodė gydytojas. Jeigu abejojate, kreipkitės į gydytoją arba vaistininką.
 
