INSERT INTO medicine VALUES(409, 'CORDARONE', 'Amjodarono hidrochloridas', '200 mg', -1, 1.0, 2.0, '-1', '-1', NULL, 'N30', 'https://vapris.vvkt.lt/vvkt-web/public/medications/view/30638', '-1', 'Visada vartokite šį vaistą tiksliai kaip nurodė gydytojas. Jeigu abejojate, kreipkitės į gydytoją arba vaistininką.
 
