INSERT INTO medicine VALUES(564, 'CINNARIZIN ACTAVIS', 'Cinarizinas', '25 mg', -1, 1.0, 2.0, '-1', '-1', NULL, 'N50', 'https://vapris.vvkt.lt/vvkt-web/public/medications/view/10378', '-1', 'Visada vartokite šį vaistą tiksliai, kaip nurodė gydytojas. Jeigu abejojate, kreipkitės į gydytoją arba vaistininką.
 
