CREATE TABLE prescription (
    prescription_nr BIGINT PRIMARY KEY NOT NULL,
    signed_date TIMESTAMP,
    SVEIDRA INT,
    professional_pi BIGINT,							-- Gydytojas
    
    visit_id BIGINT,
    compensated_code CHAR(15),
    compensated_name VARCHAR(255),
    valid_from DATE,
    prescription_drug_type_id INT NOT NULL,				-- Skiriamo vaisto tipas
    valid_till DATE,
    illness_code  CHAR(8) NOT NULL,
    illness_name VARCHAR(255) NOT NULL,
    special BOOLEAN,
    long_term BOOLEAN,
    continues BOOLEAN,
    issues_nr INT,
    
    specific_name VARCHAR(255),
    common_name VARCHAR(255) NOT NULL,
	strength VARCHAR(255) NOT NULL,               
    formative_form_id INT NOT NULL,
    use_method_id INT NOT NULL,
    atc_code CHAR(7) NOT NULL,
	atc_name VARCHAR(255) NOT NULL,
    drug_type_id INT NOT NULL,
    drug_subgroup_id INT,
    
    doses_number DECIMAL(4, 1),
    dose_name_id INT,  
    one_dose DECIMAL(3, 1),
    one_dose_form_id INT,
    use_frequency VARCHAR(255),
    use_time VARCHAR(255),
    notes_pharmaceutical VARCHAR(255),
    notes_patient VARCHAR(255),
    status_id INT
);


