  ADD CONSTRAINT prescription_con5 FOREIGN KEY (one_dose_form_id) REFERENCES one_dose_form (one_dose_form_id),
  ADD CONSTRAINT prescription_con6 FOREIGN KEY (SVEIDRA) REFERENCES health_care_facility (SVEIDRA),
